sys_script.script:
(function executeRule(current, previous /*null when async*/) {


	// If the When needed date is before rightnow, do not write the record to the database
	// Output an error message to the screen
	var nItUtils = new NeedItUtils();
	var pastDateBoolean= nItUtils.isDatePast(current.u_when_needed);
	var todayDateBoolean= nItUtils.isDateToday(current.u_when_needed);
	if(pastDateBoolean==true || todayDateBoolean==true){
		gs.addInfoMessage("When needed date cannot be in the past n today.  Your request has not been saved to the database.");
		current.setAbortAction(true);
	}


	//gs.addInfoMessage("Requested for email is :" + current.u_requested_for.email);
	//gs.addInfoMessage("requested for employee number is :" + current.u_requested_for.employee_number);
	if(gs.hasRole("admin")){
		gs.addInfoMessage("currently logged in user has the admin role");
	}	
})(current, previous);
